import React, {useState} from "react"

function App() {

  const [isRed, setRed] = useState(false)
  const [count, setCount] = useState(0)

  const increase = () => {
    setCount(count +1)
    setRed(!isRed)
    console.log(isRed)
  }

  return (
    <div>
      <h1 className='' >Color Flipper</h1>
      <button onClick={increase}>+1</button>
      <p>{count}</p>
    </div>
  )
}

export default App
