import React, {useState, useEffect} from "react"
import Recepie from "./Recepie"
import "./App.css"

function App() {

  const [recipes, setRecepies] = useState([])
  const [search, setSearch] = useState('')
  const [query, setQuery] = useState('apple')

  const appID = '0b4169b6'
  const appKey = '0d8273c165768f9b3e2e9ade7a0b8d56'
  const apiUrl = `https://api.edamam.com/api/recipes/v2?type=public&q=${query}&app_id=${appID}&app_key=${appKey}`

  useEffect(() => {
    fetchData()
  },[query])

  const fetchData = async() => {
    const resp = await fetch(apiUrl)
    const data = await resp.json()
    setRecepies(data.hits)
  }

  const storeSearch = e => {
    setSearch(e.target.value)
  }

  const storeQuery = e => {
    e.preventDefault()
    setQuery(search)
    setSearch('')
  }

  return(
    <div className="App">
      <form 
        className="search-form"
        onSubmit={storeQuery} 
        action="" >
        <input 
          className="search-input" 
          type="text" 
          value={search} 
          onChange={storeSearch} 
        />
        <button className="search-button" type="submit" >Search</button>
      </form>
      <div className="recipes">
        {recipes.map(receipt => (
          <Recepie 
            key={receipt.recipe.label} 
            titel={receipt.recipe.label} 
            calories={receipt.recipe.calories} 
            image={receipt.recipe.image}
            ingredients={receipt.recipe.ingredients}
          />
        ))}
      </div>
    </div>
  )
}

export default App
