import React, {useState, useEffect} from "react"
import Recepie from "./Recepie"

function App() {

  const appID = '0b4169b6'
  const appKey = '0d8273c165768f9b3e2e9ade7a0b8d56'
  const apiUrl = `https://api.edamam.com/api/recipes/v2?type=public&q=chicken&app_id=${appID}&app_key=${appKey}`

  const [recepies, setRecepies] = useState([])

  useEffect(() => {
    fetchData()
  },[])

  const fetchData = async() => {
    const resp = await fetch(apiUrl)
    const data = await resp.json()
    setRecepies(data.hits)
    console.log(data.hits)
  }

  return(
    <div className="App">
      <form className="search-form" action="">
        <input className="search-input" type="text" />
        <button className="search-button">Search</button>
        </form>
        {recepies.map(recepie => (
          <Recepie key={recepie.recepie.label} titel={recepie.recepie.label} calories={recepie.recepie.calories} image={recepie.recepie.image} />
        ))}
     </div>
  )
}

export default App
