import React, {useEffect, useState} from "react"
import Recepie from "./Recepie"
import "./App.css"

const App = () => {

  const [recipes, setRecipes] = useState([])
  const [search, setSearch] = useState('')
  const [query, setQuery] = useState('chicken')

  const API_ID = "0b4169b6"
  const API_KEY = "0d8273c165768f9b3e2e9ade7a0b8d56"
  const URL = `https://api.edamam.com/api/recipes/v2?type=public&q=${query}&app_id=${API_ID}&app_key=${API_KEY}`

  useEffect(() => {
    fetchData()
  },[])

  const fetchData = async() => {
    const resp = await fetch(URL)
    const data = await resp.json()
    console.log(data.hits)
    setRecipes(data.hits)
  }

  const storeSearch = e => {
    setSearch(e.target.value)
  }

  const storeQuery = e => {
    e.preventDefault()
    setQuery(search)
  }

  return (
    <div className="App">
      <form className="submit-form" onSubmit={storeQuery}>
        <input className="search-input" type="text" value={search} onChange={storeSearch}/>
        <button className="search-button" type="submit">SEARCH</button>
      </form>
      {recipes.map(recipes => (
        <Recepie 
        key={recipes.recipe.label}
        title={recipes.recipe.label}
        calories={recipes.recipe.calories}
        image={recipes.recipe.image}       
        />
      ))}
    </div>
  )
}

export default App
