import React, {useEffect, useState} from "react"
import Recepie from "./Recepie"
import { v4 as uuidv4 } from 'uuid'
import "./App.css"

const App = () => {
  console.log(uuidv4())

  const [recipes, setRecipes] = useState([])
  const [search, setSearch] = useState('')
  const [query, setQuery] = useState('chicken')

  const API_ID = "0b4169b6"
  const API_KEY = "0d8273c165768f9b3e2e9ade7a0b8d56"
  const URL = `https://api.edamam.com/api/recipes/v2?type=public&q=${query}&app_id=${API_ID}&app_key=${API_KEY}`

  useEffect(() => {
    fetchData()
  },[query])

  const fetchData = async() => {
    const resp = await fetch(URL)
    const data = await resp.json()
    setRecipes(data.hits)
  }

  const storeSearch = e => {
    setSearch(e.target.value)
  }

  const storeQuery = e => {
    e.preventDefault()
    setQuery(search)
    setSearch('')
  }

  return (
    <div className="App">

      <form 
        className="search-form" 
        onSubmit={storeQuery} 
        action="" >
      <h1 className="search-header">Find a recipe üç≤</h1>
        <input 
          className="search-bar" 
          type="text" 
          value={search} 
          onChange={storeSearch}/>
        <button className="search-button" type="submit">üîç</button>
      </form>

      <form className="options">
        <input type="checkbox" id="vegan" name="vegan" value="false" />
        <label htmlFor="vegan">Vegan</label>
        <input type="checkbox" id="vegetarian" name="vegetarian" value="false" />
        <label htmlFor="vegetarian">Vegetarian</label>
        <input type="checkbox" id="gluten" name="gluten" value="false" />
        <label htmlFor="gluten">Gluten-free</label>
        <input type="checkbox" id="lactose" name="lactose" value="false" />
        <label htmlFor="lactose">Lactose-free</label>
      </form>

      <div className="recipes">
        {recipes.map(recipes => (
          <Recepie
          key={uuidv4()}
          title={recipes.recipe.label}
          calories={recipes.recipe.calories}
          image={recipes.recipe.image}
          ingredients={recipes.recipe.ingredients}     
          />
        ))}
      </div>

    </div>
  )
}

export default App
